class Solution {
    public void setZeroes(int[][] matrix) {
        //if we do normal approach whole matrix becomes 0
        // Basic explanation:
        // we are using the first row and column as a memory to keep track of all the 0's in the entire matrix.

        if(matrix==null || matrix.length==0 || matrix[0].length==0){
            return;
        }


        //first we have to check is there any need to make first row and first column
        //so that at last we make tham 0
        int m=matrix.length;
        int n=matrix[0].length;
        boolean first_row=false;
        boolean first_col=false;
        for(int i=0;i<m;i++){
            if(matrix[i][0]==0){
                first_col=true;
                break;
            }
        }
        for(int j=0;j<n;j++){
            if(matrix[0][j]==0){
                first_row=true;
                break;
            }
        }
        //now find 0 inside matrix (except first row and col)
        //make 0 at the corresponding pos of first row and first col
        for(int i=1;i<m;i++){
            for(int j=1;j<n;j++){
                if(matrix[i][j]==0){
                    matrix[i][0]=0;
                    matrix[0][j]=0;
                }
            }
        }
        //Now it's time to make them 0 based to first row and col
        for(int i=1;i<m;i++){
            for(int j=1;j<n;j++){
                if(matrix[i][0]==0 || matrix[0][j]==0){
                    matrix[i][j]=0;
                }
            }
        }
        //At last convert first row 0 if first_row is true
        if(first_row){
            for(int j=0;j<n;j++){
                matrix[0][j]=0;
            }
        }
        //same for first col
        if(first_col){
            for(int i=0;i<m;i++){
                matrix[i][0]=0;
            }
        }
    }
}
