void setZeroes(vector<vector<int>>& matrix) 
{
        int m = matrix.size(), n = matrix[0].size();
        bool isRowZero = false, isColZero = false;

		// Check the first Column, zero is present or not...
        for(int i = 0; i < m; i++)
        {
            if(matrix[i][0] == 0)
            {
                isColZero = true;
                break;
            }
        }

		// check the First Row, zero is present or not
        for(int i = 0; i < n; i++)
        {
            if(matrix[0][i] == 0)
            {
                isRowZero = true;
                break;
            }
        }

		// check all elements except first row & first Column
        for(int i = 1; i < m; i++)
        {
            for(int j = 1; j < n; j++)
            {
                if(matrix[i][j] == 0)
                {
                    matrix[i][0] = 0;
                    matrix[0][j] = 0;
                }
            }
        }

		// process all elements except first row & first column
        for(int i = 1; i < m; i++)
        {
            for(int j = 1; j < n; j++)
            {
                if(matrix[i][0] == 0 || matrix[0][j] == 0)
                    matrix[i][j] = 0;
            }
        }

		// process first column
        if(isColZero)
        {
            for(int i = 0; i < m; i++)
                matrix[i][0] = 0;
        }

		// process first row
        if(isRowZero)
        {
            for(int i = 0; i < n; i++)
                matrix[0][i] = 0;
        }
}
```

