class Solution {
private:
    void makeRowZero(vector<vector<int>>& matrix, int row, int col) {
        for (int j = 0; j < col; j++)
            matrix[row][j] = 0;    
    }  
    void makeColZero(vector<vector<int>>& matrix, int row, int col) {
        for (int i = 0; i < row; i++)
            matrix[i][col] = 0;    
