            if(matrix[i][0] == 0){
                colZero = true;
                break;
            }
        }

        // check for first row 
        for(int j=0;j<colSize;j++){
            if(matrix[0][j] == 0){
                rowZero = true;
                break;
            }
        }
        // mark 0 in first row/col where the remaining part of matrix has zero or not.
        for(int i=1;i<rowSize;i++){
            for(int j=1;j<colSize;j++){
                if(matrix[i][j] == 0){
                    matrix[0][j] = 0;
                    matrix[i][0] = 0;
                }
            }
        }
        // setting the matrix as 0 wherever required except the first row or col
        for(int i=1;i<rowSize;i++){
            for(int j=1;j<colSize;j++){
                if(matrix[0][j] == 0|| matrix[i][0] == 0) matrix[i][j] = 0;
            }
        }

        if(rowZero){
            for(int j=0;j<colSize;j++) matrix[0][j] = 0;
        }
        if(colZero){
            for(int i=0;i<rowSize;i++) matrix[i][0] = 0;
        }

    }
