	void setZeroes(vector<vector<int>>& A) {
        int n = A.size();
        if(n == 0)
            return;
        int m = A[0].size();

        queue<pair<int,int>> q;
        for(int i=0; i<n; i++)
            for(int j=0; j<m; j++)
                if(A[i][j] == 0)
                    q.push({i,j});

        while(!q.empty())
        {
            int f = q.front().first;
            int s = q.front().second;
            q.pop();

            for(int i=0; i<n; i++)
                A[i][s] = 0;

            for(int j=0; j<m; j++)
                A[f][j] = 0;
        }
    }
