class Solution {
    public void setZeroes(int[][] matrix) {

        boolean ispresent = false;
        for(int i=0; i<matrix.length; i++){
            if (matrix[i][0] == 0) ispresent = true;
            for(int j=1; j<matrix[0].length; j++){
                if(matrix[i][j] == 0){
                    matrix[0][j] = matrix[i][0] = 0;
                }
