```
class TreeNode(Iterable):

    def __init__(
        self, 
        val: int = 0, 
        left: Optional["TreeNode"] = None, 
        right: Optional["TreeNode"] = None
    ):

        self.val = val
        self.left = left
        self.right = right

    def __iter__(self) -> Iterator:

        return BSTIterator(self)

    def __str__(self) -> str:
        return str(self.val)


class BSTIterator(Iterator):

    def __init__(self, root: Optional[TreeNode] = None) -> None:
        self._current = root

    def next(self) -> Optional[int]:

        val = None
        while self._current:
            if self._current.left:
                predecessor = self._current.left
                while predecessor.right and predecessor.right != self._current:
                    predecessor = predecessor.right

                if predecessor.right is None:
                    predecessor.right = self._current
                    self._current = self._current.left
                else:
                    val = self._current.val
                    predecessor.right = None
                    self._current = self._current.right
                    break
            else:
                val = self._current.val
                self._current = self._current.right 
                break

        return val 

    def hasNext(self) -> bool:

        return self._current is not None 

    def __next__(self) -> int:

        val = self.next()
        if val is None:
            raise StopIteration

        return val

