class BSTIterator {
    stack<TreeNode*> st;
    TreeNode* visit;
public:
    BSTIterator(TreeNode* root) {
        visit = root;
    }
    int next() {
        while(visit){
            st.push(visit);
            visit = visit->left;
        }
        TreeNode* next = st.top(); st.pop();
        visit = next->right;
        return next->val;
    }
    bool hasNext() {
        return visit || !st.empty();
    }
    void pushLeft(TreeNode* root){
        while(root){
            st.push(root);
            root=root->left;
        }
    }
};

