class BSTIterator {
private: 
    vector<int> list;
    int loc;
public:
    BSTIterator(TreeNode* root) {
        loc = 0;
        dfs(root);
    }
    void dfs(TreeNode* root){
        if(!root) return;
        dfs(root->left);
        list.push_back(root->val);
        dfs(root->right);
    }
    int next() {
        return list[loc++];
    }
    bool hasNext() {
        if(loc<list.size()) return true;
        else return false;
    }
};
```

