public:
    TreeNode* node;
    BSTIterator(TreeNode* root) {
        node = root;
        while(node != NULL){
            if(node->left == NULL){
                node = node->right;
            }else{
                TreeNode* temp = node->left;
                while(temp->right!=NULL && temp->right != node){
                    temp = temp->right;
