class BSTIterator {
    ArrayList<Integer> list=new ArrayList<>();
    int i=0;
    public BSTIterator(TreeNode root) {
        inorder(root);
    }
    public void inorder(TreeNode root){
        if(root==null)
            return;
        inorder(root.left);
        list.add(root.val);
        inorder(root.right);
    }

    public int next() {
        return list.get(i++);
    }

    public boolean hasNext() {
        if(i<list.size())
            return true;
        return false;
    }
}
