class BSTIterator {
    stack<TreeNode*> s;
public:
    BSTIterator(TreeNode* root) {
        push_left_nodes(root);
    }
    void push_left_nodes(TreeNode* node)
    {
        while(node)
        {
            s.push(node);
            node = node->left;
        }
    }
    // TC - average O(1) SC - O(h)
    int next() {
        TreeNode* left = s.top();
        s.pop();
        push_left_nodes(left->right);
        return left->val;
    }
    // TC - O(1)
    bool hasNext() {
        return !s.empty();
    }
};
```

