class BSTIterator {
public:
    stack<TreeNode*> st;

    BSTIterator(TreeNode* root) {
        st.push(root);

        while(root->left)
        {
            st.push(root->left);
            root=root->left;
        }
    }

    int next() {
        TreeNode *root=st.top();
        st.pop();

        int ans=root->val;

        if(root->right)
        {
            root=root->right;
            st.push(root);
            while(root->left)
            {
                st.push(root->left);
                root=root->left;
            }
        }

        return ans;
    }

    bool hasNext() {
        return !st.empty();
    }
};
```

