class BSTIterator {
public:
    TreeNode *head,*t; 

    void convert(TreeNode* root)
    {
        if(!root)
            return;

        convert(root->left);

        t->right=root;
        t=t->right;

        convert(root->right);
    }

    BSTIterator(TreeNode* root) {

        head=new TreeNode(0);
        t=head;
        convert(root);
    }

    int next() {
        head=head->right;
        return head->val;
    }

    bool hasNext() {
        return (head->right!=NULL);
    }
};
```

