class BSTIterator(object):

    def __init__(self, root):
        """
        :type root: TreeNode
        """
        def inorder(node):
            if not node: return
            for val in inorder(node.left):
                yield val
            yield node.val
            for val in inorder(node.right):
                yield val
        self._iter=inorder(root)
        self._buffer=None # peeked buffer        

    def next(self):
        """
        @return the next smallest number
        :rtype: int
        """
        if self.hasNext():
            rlt=self._buffer
            self._buffer=None
            return rlt
        else:
            return None


    def hasNext(self):
        """
        @return whether we have a next smallest number
        :rtype: bool
        """
        if self._buffer is None:
            self._buffer=next(self._iter,None)

        if self._buffer!=None:
            return True
        else:
            return False

