class BSTIterator {
public:
    stack<TreeNode*> st;

    BSTIterator(TreeNode* root) {
        while(root) {
            st.push(root);
            root = root -> left;
        }
    }

    int next() {
       if(!st.empty()) {
           TreeNode *top = st.top(); st.pop();
           TreeNode *next = top -> right;
            while(next) {
                st.push(next);
                next = next -> left;
            }
           return top -> val;
       }
       return -1;
    }

    bool hasNext() {
        if(!st.empty()) return true;
        return false;
    }
};
