class BSTIterator
{//iterative inorder traversal (inorder of BST is sorted)
    ArrayDeque<TreeNode> stack;//Faster than stack 

    private void pushAll(TreeNode temp){
        //pushing the left nodes on the stack until we reach the null node 
        while(temp != null){
            stack.addLast(temp);//pushing to the stack 
            temp= temp.left;//moving the left subtree
        }
    }

    public BSTIterator(TreeNode root) {//parameterised constructor 
        stack= new ArrayDeque<>();//intializing stack 
        pushAll(root);
    }

    public int next(){
        TreeNode pop= stack.removeLast();//smallest element till now 
        pushAll(pop.right);//pushing all the right node in inorder fashion
        return pop.val;
    }

    public boolean hasNext() {
        if(!stack.isEmpty())//if stack is empty there is no element in the stack, otherwise there is atleast one 
            return true;
        return false;
    }
}//Please do Upvote, it helps alot
