    def __init__(self, root: TreeNode):
        def inorder(root):
            if root:
                yield from inorder(root.left)
                yield root.val
                yield from inorder(root.right)
        self.iterable = inorder(root)
        self._next = next(self.iterable, None)

    def next(self) -> int:
        _next = self._next
        self._next = next(self.iterable, None)
        return _next

    def hasNext(self) -> bool:
        return self._next is not None
```

