        if(!curr)return;
        fill(curr->left);
        inorder.push_back(curr->val);
        fill(curr->right);
    }

    int next() {
        if(idx<inorder.size()){
            int val=inorder[idx++];
            return val;
        }
        else{
            return INT_MAX;
        }

    }

    bool hasNext() {
        return idx<=inorder.size()-1?true:false;
    }
};

