/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class BSTIterator {
public:
    /* 
       - we will do an inorder traversal and store the elements in a queue
       - it will allow us to keep the lowest element in the front
       - we will fetch from the queue for every next call
       - has next will check whether the queue is empty
    */
    queue<int>inOrder;
    BSTIterator(TreeNode* root) {
        getInorder(root);
    }

    int next() {
        if(inOrder.size() != 0){
            int res = inOrder.front();
            inOrder.pop();
            return res;
        }
        return 0;
    }

    bool hasNext() {
        return inOrder.size() > 0;
    }

    void getInorder(TreeNode* root){
        if(!root) return;
        getInorder(root->left);
        inOrder.push(root->val);
        getInorder(root->right);
    }
};

