class BSTIterator {

    ArrayList<Integer> tree;
    int l = 0;
    public BSTIterator(TreeNode root) {
        tree = new ArrayList<>();
        inorder(root);
    }

    public int next() {
        return tree.get(l++);
    }

    public boolean hasNext() {
        return l < tree.size();
    }

	// adds node values to ArrayList (tree)
    private void inorder(TreeNode root) {
        if (root != null) {
            inorder(root.left);
            tree.add(root.val);
            inorder(root.right);
        }
    }
}
