class BSTIterator {
public:

    stack<TreeNode*> st;

    BSTIterator(TreeNode* root) {    
	//To initialize stack
        stackFill(root);
    }

    int next() {
	//returns next inorder element and push all left elements which are to the right of the node.
        TreeNode* node = st.top();
        st.pop();
        stackFill(node->right);

        return node->val;
    }

    bool hasNext() {
	//return true when stack is not empty else false
        return !st.empty();
    }

	//Initialize stack with all elements left to the root, including root
    private: 
        void stackFill(TreeNode* root){

            while(root){
                st.push(root);
                root = root->left;
            }
        }
};

