var numberToWords = function(num) {
  if (num === 0) {
    return 'Zero';
  }

  if (num <= 20) {
    return translations.get(num);
  }

  let result = [];

  for (let [value, translation] of translations) {
    const times = Math.floor(num / value);

    if (times === 0) {
      continue;
