    num -= times * value;

    if (times === 1 && value >= 100) {
      result.push('One', translation);
      continue;
    }

    if (times === 1) {
      result.push(translation);
      continue;
    }

    result.push(numberToWords(times), translation);
  }

  return result.join(' ');
};

