            num %= thousand;
        }
        if (num >= hundred)
        {
            result.append("" + numberToWords(num / hundred) + " Hundred ");
            num %= hundred;
        }
        if (num != 0)
        {
            result.append(less100(num));
        }

        return result.toString().trim();
