class Solution {
private:
    vector<string> under20 = {
        "", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine", 
        "Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen", "Sixteen", "Seventeen", "Eighteen", "Nineteen"
    };
    vector<string> under100 = {
        "", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"
    };
private:
    string helper(int num) {
        if (num < 20) {
            return under20[num];
        } else if (num < 100) {
            return under100[num / 10] + " " + helper(num % 10);
        } else if (num < 1000) {
            return helper(num / 100) + " Hundred " + helper(num % 100);
        } else if (num < (int)1e6) {
            return helper(num / 1000) + " Thousand " + helper(num % 1000);
        } else if (num < (int)1e9) {
            return helper(num / (int)1e6) + " Million " + helper(num % (int)1e6);
        } else {
            return helper(num / (int)1e9) + " Billion " + helper(num % (int)1e9);
        }
    }
public:
    string numberToWords(int num) {
        if (num == 0)
            return "Zero";
        stringstream ss = (stringstream)helper(num);
        string data;
        string ans = "";
        while(getline(ss, data, ' ')) {
            if (data.size()) ans += data + " ";
        }
        ans.pop_back();
        return ans;
    }
};
