class Solution {
public:
    //Write funtion for 3 digit number first and then add billion, million & thousand to it.
    string digitsToWord[10]  = {"", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"};
    string tensToWord[10] = {"", "teen", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty","Ninety"};
    string wordFor10to19[10] = {"Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen", "Sixteen", "Seventeen", "Eighteen", "Nineteen"};
    string billMillThou[4] = {"Billion", "Million", "Thousand", ""};
    string convertThreeDigitsToWord(int num){
        int hun = num/100, tens = (num%100)/10, unit = num%10};
        string s = "";
        if(hun != 0) s += digitsToWord[hun] + " Hundred";
        if(tens == 1){
            if(s!= "") s+= " ";
            s += wordFor10to19[unit];
        }else{
            if(tens != 0) {
                if(s!= "") s+= " ";
                s += tensToWord[tens];
            }
            if(unit != 0){
                if(s!= "") s+= " ";
                s += digitsToWord[unit]; 
            } 
        }
        return s;
    }

    string numberToWords(int num) {
        if(num == 0) return "Zero";
        string ans = "";
        int billion = 1e9, million = 1e6, thousand = 1e3;
        int numArr[4] = { num/billion, (num % billion)/million, (num % million)/thousand, num % thousand };

        for(int i = 0; i < 4; ++i){
            if(numArr[i] != 0) {
                if(ans != "") ans += " ";
                ans += convertThreeDigitsToWord(numArr[i]) + billMillThou[i];
            }
        }
        return ans;
    }
}; 
