    static {
        getMap(map);
    }

    public String numberToWords(int num) {
        if(num == 0) return "Zero";
        StringBuilder sb = new StringBuilder();
        while(num > 0) {
           Integer key = map.floorKey(num);
           Integer n = num/key;
            if(key < 100 && map.containsKey(key)) {
                sb.append(" ").append(map.get(key));
            } else if(n < 100 && map.containsKey(n)) {
                sb.append(" ").append(map.get(n)).append(" ").append(map.get(key));
            } else {
                sb.append(" ").append(numberToWords(n)).append(" ").append(map.get(key));
