    {9, "Nine"},{8, "Eight"},{7, "Seven"},{6, "Six"}, {5, "Five"}, {4, "Four"}, {3, "Three"}, {2, "Two"}, {1, "One"}
};
    string numberToWords(int num) {
        if (num == 0) return "Zero";
        for(auto it:nms){
            if(num>=it.first){
                string a="";
                if(num>=100){
                    a=numberToWords(num/it.first)+" ";
                }
                string b=it.second;
                string c="";
                if(num%it.first!=0){
                    c=" "+numberToWords(num % it.first);
                }
                return a+b+c;
            }

        }
        return "" ;
    }

};
