var numberToWords = function(num) {
    if (num === 0) return 'Zero';
    const map = {
        1: 'One',
        2: 'Two',
        3: 'Three',
        4: 'Four',
        5: 'Five',
        6: 'Six',
        7: 'Seven',
        8: 'Eight',
        9: 'Nine',
        10: 'Ten',
        11: 'Eleven',
        12: 'Twelve',
        13: 'Thirteen',
        14: 'Fourteen',
        15: 'Fifteen',
        16: 'Sixteen',
        17: 'Seventeen',
        18: 'Eighteen',
        19: 'Nineteen',
        20: 'Twenty',
        30: 'Thirty',
        40: 'Forty',
        50: 'Fifty',
        60: 'Sixty',
        70: 'Seventy',
        80: 'Eighty',
        90: 'Ninety',
    }

    const billion = 1000000000;
    const million = 1000000;
    const thousand = 1000;
    const hundred = 100;
    const tens = 10;

    const convertNum = (num) => {
        let str = '';
        if (num >= hundred) {
            str += map[Math.floor(num / hundred)] + ' Hundred ';
            num = num % hundred;
        }
        if (num >= tens) {
            if (map[num]) {
                str += map[num] + ' ';
                num -= num;
            } else {
                str += map[Math.floor(num / tens) * tens] + ' ';
                num = num % tens;
            }
        }
        if (num >= 1) {
            str += map[num];
        }
        return str.trim();
    }

    let str = '';
    if (num >= billion) {
        str += convertNum(Math.floor(num / billion)) + ' Billion ';
        num = num % billion;
    }
    if (num >= million) {
        str += convertNum(Math.floor(num / million)) + ' Million ';
        num = num % million;
    }
    if (num >= thousand) {
        str += convertNum(Math.floor(num / thousand)) + ' Thousand ';
        num = num % thousand;
    }
    str += convertNum(Math.floor(num))
    return str.trim();
};
