    string getT(int n) {
        auto t = n / 10;
        auto o = n % 10;
        if (n >= 11 && n < 20) 
            return ele[o];
        if (t && o) 
            return tns[t] + " " + ons[o];
        if (t) 
            return tns[t];
        if (o) 
            return ons[o];
        return "";
    }

    string getH(int n) {
        auto h = n / 100;
        auto t = n % 100;
        if (h && t) 
            return ons[h] + " Hundred " + getT(t);
        if (h)
            return ons[h] + " Hundred";
        if (t) 
            return getT(t);
        return "";
    }

    string numberToWords(int num) {
        if (num == 0)
            return "Zero";
        string ret = "";
        auto i = 0;
        while (num) {
            auto h = num % 1000;
            if (h)
                ret = getH(h) + grp[i] + (ret.size()? " ": "") + ret;
            i++;
            num /= 1000;
        }
        return ret;
    }
