        num /= 1000;
        if(num % 1000 != 0)
        {
            words.push_back(int2str(num % 1000) + " Billion");
        }
        std::string result;
        while(!words.empty())
        {
            result += words.back();
            result.push_back(' ');
            words.pop_back();
