class Solution {
public:

    // conver a num less than 1000 to word 
    string hundred_to_word(int num) {
        string ret;
        int hundred = num / 100;
        int ten = num / 10 % 10;
        int one = num % 10;
        switch (hundred) {
        case 0: break;
        case 1: ret = "One Hundred"; break;
        case 2: ret = "Two Hundred";   break;
        case 3: ret = "Three Hundred"; break;
        case 4: ret = "Four Hundred"; break;
        case 5: ret = "Five Hundred"; break;
        case 6: ret = "Six Hundred"; break;
        case 7: ret = "Seven Hundred"; break;
        case 8: ret = "Eight Hundred"; break;
        case 9: ret = "Nine Hundred"; break;
        }
        if (hundred && (ten || one)) {
            ret += " ";
        }
        if (ten == 1) {
            switch (one) {
            case 0: return ret += "Ten";
            case 1: return ret += "Eleven";
            case 2: return ret += "Twelve";
            case 3: return ret += "Thirteen";
            case 4: return ret += "Fourteen";
            case 5: return ret += "Fifteen";
            case 6: return ret += "Sixteen";
            case 7: return ret += "Seventeen";
            case 8: return ret += "Eighteen";
            case 9: return ret += "Nineteen";
            }
        } else if (ten) {
            switch (ten) {
            case 2: ret += "Twenty"; break;
            case 3: ret += "Thirty"; break;
            case 4: ret += "Forty"; break;
            case 5: ret += "Fifty"; break;
            case 6: ret += "Sixty"; break;
            case 7: ret += "Seventy"; break;
            case 8: ret += "Eighty"; break;
            case 9: ret += "Ninety"; break;
            }
        }
        if (!one) {
            return ret;
        } else {
            if (ten) {
                ret += " ";
            }
            switch (one) {
            case 1: ret += "One"; break;
            case 2: ret += "Two"; break;
            case 3: ret += "Three"; break;
            case 4: ret += "Four"; break;
            case 5: ret += "Five"; break;
            case 6: ret += "Six"; break;
            case 7: ret += "Seven"; break;
            case 8: ret += "Eight"; break;
            case 9: ret += "Nine"; break;
            }
        }
        return ret;
    }

    string numberToWords(int num) {
        if (num == 0) { // edge case
            return "Zero";
        }

        int billion = num / 1000000000;
        int million = num / 1000000 % 1000;
        int thousand = num / 1000 % 1000;
        int hundred = num % 1000; // anything less than 1000 is considered together
        string ans;
        stringstream ss;

        if (billion) {
            ss << hundred_to_word(billion) << " Billion";
        }
        if (million) {
            if (billion) {
                ss << " ";
            }
            ss << hundred_to_word(million) << " Million";
        }
        if (thousand) {
            if (billion || million) {
                ss << " ";
            }
            ss << hundred_to_word(thousand) << " Thousand";
        }
        if (hundred) {
            if (billion || million || thousand) {
                ss << " ";
            }
            ss << hundred_to_word(hundred);
        }
        return ss.str();
    }
};
