public class Solution
{
    public string NumberToWords(int num)
        => string.Join(" ", NumberToWordsRecursive(num).DefaultIfEmpty("Zero"));

    private IEnumerable<string> NumberToWordsRecursive(int num)
    {
        foreach (var (val, str) in majors.Where(t => num >= t.val))
            return NumberToWordsRecursive(num / val).Append(str).Concat(NumberToWordsRecursive(num % val));

        return num == 0 ? Enumerable.Empty<string>() // 0
             : num < 20 && num != 10 ? new[] { words[num % 10 - 1, num / 10] } // 1..9, 11..19
             : NumberToWordsRecursive(num % 10).Prepend(words[num / 10 - 1, 2]); // 10, 20..99
    }

