            ans += " ";
            ans += numberToWords(num%val);
        }
        return ans;
    }
    string numberToWords(int num) {

        if(num >= billions)
            return numberToWords(num, billions, 3);
        if(num >= millions)
            return numberToWords(num, millions, 2);
        if(num >= thousands)
            return numberToWords(num, thousands, 1);
        if(num >= hundreds)
            return numberToWords(num, hundreds, 0);
        if(num < 20) 
            return ones[num];
        int n = num/10;
        string ans = tens[n];
        if(num%10 != 0) {
            ans += " ";
            ans += ones[num%10];
        }
        return ans;
