        {
            pair<int,pair<int,int>> tp = pq.top();
            int x = tp.second.first, y = tp.second.second;
            pq.pop();
            ans.push_back({nums1[x], nums2[y]});
            if(y != m-1)
                pq.push({nums1[x]+nums2[y+1],{x, y+1}});
        }
        return ans;
    }
};
