struct pair_hash {
    template<class T1, class T2>
    size_t operator () (const pair<T1, T2>& p) const {
        auto h1 = hash<T1>{}(p.first);
        auto h2 = hash<T2>{}(p.second);
        return h1 ^ h2;
    }
};

class Solution {
public:
    vector<vector<int>> kSmallestPairs(vector<int>& A, vector<int>& B, int k) {
        int n = A.size();
        int m = B.size();
        priority_queue<vector<int>, vector<vector<int>>, greater<vector<int>>> pq;
        unordered_set<pair<int, int>, pair_hash> hashSet;
        vector<vector<int>> ans;
        pq.push({A[0] + B[0], 0, 0});
        hashSet.insert(make_pair(0, 0));
        while (!pq.empty() && k-- > 0) {
            vector<int> minValue = pq.top();
            pq.pop();
            int i = minValue[1];
            int j = minValue[2];
            ans.push_back({A[i], B[j]});
            if (i < n - 1 && hashSet.find(make_pair(i + 1, j)) == hashSet.end()) {
                pq.push({A[i + 1] + B[j], i + 1, j});
                hashSet.insert(make_pair(i + 1, j));
            }

            if (j < m - 1 && hashSet.find(make_pair(i, j + 1)) == hashSet.end()) {
                pq.push({A[i] + B[j + 1], i, j + 1});
                hashSet.insert(make_pair(i, j + 1));
            }
        }
        return ans;
    }
};
