    			one.sum = nums1[i] + nums2[j];
    			pq.add(one);
    		}
    	}
    	while(!pq.isEmpty() && k-->0) {
    		List<Integer> list = new ArrayList<Integer>();
    		Node one = pq.poll();
    		list.add(one.a);
    		list.add(one.b);
    		res.add(list);
    	}
    	return res;
    }

