#include <vector>
#include <utility>
#include <queue>

using namespace std;

class Solution {
public:
    vector<pair<int, int>> kSmallestPairs(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<pair<int, int>> res;
        if (nums1.empty() || nums2.empty()) {
            return res;
        }

        auto comp = [&](auto& pr1, auto& pr2) {
            int sum1 = nums1[pr1.first] + nums2[pr1.second];
            int sum2 = nums1[pr2.first] + nums2[pr2.second];

            return sum2 < sum1;
        };

        priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(comp)> pq(comp);

        int n1 = nums1.size();
        for (int i = 0; i < n1; ++i) {
            pq.push({i, 0});
        }

        int n2 = nums2.size();
        while (!pq.empty() && res.size() < (size_t)k) {
            auto pr = pq.top();
            pq.pop();

            res.push_back({nums1[pr.first], nums2[pr.second]});

            if (pr.second + 1 < n2) {
                pq.push({pr.first, pr.second + 1});
            }
        }

        return res;

    }
};
