import heapq
class Solution:
    def kSmallestPairs(self, nums1: List[int], nums2: List[int], k: int) -> List[List[int]]:
        if not nums1 or not nums2:
            return []
        res = []
        len_nums1, len_nums2 = len(nums1), len(nums2)
        my_hp = [[nums1[0]+nums2[0], 0, 0]] # the index of both nums1 and nums2 are also included in heap item.
        seen = {(0,0)}
        while k > 0 and my_hp:
            _, left, right = heappop(my_hp)
            res.append([nums1[left], nums2[right]])
            k -= 1
            if left < len_nums1-1 and (left+1, right) not in seen:
                heappush(my_hp, [nums1[left+1]+nums2[right], left+1, right])
                seen.add((left+1, right))
            if right < len_nums2-1 and (left, right+1) not in seen:
                heappush(my_hp, [nums1[left]+nums2[right+1], left, right+1])
                seen.add((left, right+1))
        return res
