        while(!pq.empty() && k--)
        {
            auto curr = pq.top();

            pq.pop();

            int i = curr.second.first;

            int j = curr.second.second;

            res.push_back({nums1[i], nums2[j]});

            if(j + 1 < n2)
            {
                pq.push({nums1[i] + nums2[j + 1], {i, j + 1}});
            }
        }

        return res;
    }
};
