class Solution {
public:
    vector<vector<int>> kSmallestPairs(vector<int>& v1, vector<int>& v2, int k) {
        sort(v1.begin(), v1.end());
        sort(v2.begin(), v2.end());
        n1 = v1.size(), n2 = v2.size();
        if (!n1 || !n2) return res;

        priority_queue<tuple<int, int, int>, vector<tuple<int, int, int>>, greater<tuple<int, int, int>>> pq;
        for (int i = 0; i < n1; i++) {
            pq.emplace(v1[i]+v2[0], i, 0);
        }
        int v = 0, i = 0, j = 0, cnt = 0;
        while (cnt < k && pq.size()) {
            auto t = pq.top();
            pq.pop();
            v = get<0>(t), i = get<1>(t), j = get<2>(t);
            if (j+1 < n2)
                pq.emplace(v1[i]+v2[j+1], i, j+1);       
            res.emplace_back(vector<int>{v1[i], v2[j]});
            cnt++;
        }
        return res;
    }    
    int n1, n2;
    vector<vector<int>> res;
};

