class Solution {
public:
    // Define a type alias for convenience
    using int2 = pair<int, int>;

    // Custom hash function for the int2 pair
    struct PairHash {
        size_t operator () (const int2& p) const {
            // Use XOR (^) operation to combine the hash of the first element and the second element
            return hash<int>{}(p.first) ^ p.second;
        }
    };

    vector<vector<int>> kSmallestPairs(vector<int>& nums1, vector<int>& nums2, int k) 
    {
        // Get the sizes of the input vectors
        int n1 = nums1.size(), n2 = nums2.size();

        // Define a lambda function to compare two vectors based on their sums
        function<bool(vector<int>&, vector<int>&)> cmp = [](vector<int>& x, vector<int>& y) -> bool
        { 
            return (x[0] + x[1]) > (y[0] + y[1]); 
        };

        // Create a priority queue to store the k smallest pairs, using the custom comparison function
        priority_queue<vector<int>, vector<vector<int>>, decltype(cmp)> pq(cmp);

        // Create a vector to store the resulting pairs
        vector<vector<int>> ans;
        ans.reserve(k);

        // Create an unordered set to keep track of visited pairs using custom hash function
        unordered_set<int2, PairHash> visited;

        // Push the initial pair into the priority queue and mark it as visited
        pq.push({nums1[0], nums2[0], 0, 0});
        visited.insert({0, 0});

        // Main loop to find the k smallest pairs
        while (!pq.empty()) {
            // Extract the top pair from the priority queue
            vector<int> x = pq.top();
            pq.pop();

            // Extract the elements and indices from the pair
            int p0 = x[0], p1 = x[1], i = x[2], j = x[3];

            // Decrement k and add the pair to the result vector
            k--;
            ans.push_back({p0, p1});

            // Check if we have found k pairs, if yes, return the result vector
            if (k == 0) return ans;

            // Check the adjacent pairs and push them into the priority queue if they are valid and not visited
            if (i + 1 < n1 && visited.count({i + 1, j}) == 0) {
                pq.push({nums1[i + 1], nums2[j], i + 1, j});
                visited.insert({i + 1, j});
            } 
            if (j + 1 < n2 && visited.count({i, j + 1}) == 0) {
                pq.push({nums1[i], nums2[j + 1], i, j + 1});
                visited.insert({i, j + 1});
            } 
        }

        // Return the resulting pairs
        return ans;
    }
};

