var kSmallestPairs = function(nums1, nums2, k) {
    if (!nums1.length || !nums2.length) return [];

    let heap = new MaxBinaryHeap();

    for (let i = 0; i < nums1.length; i++) {
        for (let j = 0; j < nums2.length; j++) {
            let elem = {
                index: [nums1[i], nums2[j]],
                val: nums1[i] + nums2[j]
            }

            if (heap.size < k) {
