public List<List<Integer>> kSmallestPairs(int[] nums1, int[] nums2, int k) {
		List<List<Integer>> res = new ArrayList<>();
		if (nums1.length == 0 || nums2.length == 0 || k == 0) return res;
		// Using lambda function to pass list as element to pq, while maintaining as minHeap
		PriorityQueue<List<Integer>> pq = new PriorityQueue<>((a,b) -> (a.get(0) + a.get(1) - b.get(0) - b.get(1)));
		for (int i = 0; i < nums1.length && i < k; i++) {
			// initialize new list to add element, with the third element as nums2's index  
			List<Integer> list = new ArrayList<>();
			list.add(nums1[i]);
			list.add(nums2[0]);
			list.add(0);
			pq.offer(list);
		}

		while (!pq.isEmpty() && k-- > 0) {
			List<Integer> cur = pq.poll(); 
			List<Integer> list = new ArrayList<>();
			list.add(cur.get(0));
			list.add(cur.get(1));
			res.add(list);

			if (cur.get(2) + 1 == nums2.length) continue;
			List<Integer> tmp = new ArrayList<>();
			tmp.add(cur.get(0));
			tmp.add(nums2[cur.get(2) + 1]);
			tmp.add(cur.get(2) + 1);
			pq.offer(tmp);

		}
		return res;
	}

