    bool operator()(vector<int>& v1, vector<int>& v2){
        return v1[0]>v2[0];
    }
};
class Solution {
public:
    vector<vector<int>> kSmallestPairs(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<vector<int>>res;
        priority_queue<vector<int>,vector<vector<int>>,compare>pq;
        map<pair<int,int>,int>visited;
        if(0<nums1.size() and 0<nums2.size()){
            pq.push({nums1[0]+nums2[0],0,0});
            visited[make_pair(0,0)]=1;
        }
        int count=0;
        while(!pq.empty()){
            vector<int>f=pq.top();
            pq.pop();
            res.push_back({nums1[f[1]],nums2[f[2]]});
            count++;
            if(count==k){
                break;
            }
            if(f[1]<nums1.size() and f[2]+1<nums2.size() and visited.count(make_pair(f[1],f[2]+1))==0){
                pq.push({nums1[f[1]]+nums2[f[2]+1],f[1],f[2]+1});
                visited[make_pair(f[1],f[2]+1)]=1;
