 vector<vector<int>> kSmallestPairs(vector<int>& nums1, vector<int>& nums2, int k) {
        //we will have two loops here to make all possible pairs
        //we will have a max heap of pair with first as sum and second as that pair.
        //we will push k smallest in ans vector.
        priority_queue<pair<int,pair<int,int>>>mh;
        int n = nums1.size();
        int m = nums2.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int sum = nums1[i]+nums2[j];
                //how to treat TLE
                if(mh.size()<k){
                    mh.push({sum,{nums1[i],nums2[j]}});
                }
                else if (sum<mh.top().first) {
                    mh.pop();
                    mh.push({sum,{nums1[i],nums2[j]}});
                } else {
                    break;  // since it is sorted so we can say that rest of vector 2 should have greater sum than this ,so we break inner loop, so it took less time.
                }
            }
        }
        vector<vector<int>>ans;
        while(!mh.empty()){
            ans.push_back({mh.top().second.first,mh.top().second.second});
            mh.pop();
        }
        return ans;
        //TC  = O(n*mlogk)
        //SC = O(k)
    }
