class Solution:
    def kSmallestPairs(self, nums1: List[int], nums2: List[int], k: int) -> List[List[int]]:
        heap = [[nums1[0] + nums2[0], 0, 0]] #[sum, nums1_index, nums2_index]
        seen = set()
        ans = []

        while len(ans) < min(len(nums1) * len(nums2), k):
            lastAns = heapq.heappop(heap)
            while (str(lastAns) in seen):
                lastAns = heapq.heappop(heap)

            seen.add(str(lastAns))
            ans.append([nums1[lastAns[1]], nums2[lastAns[2]]])

            if (lastAns[1] + 1 < len(nums1)):
                sum = nums1[lastAns[1] + 1] + nums2[lastAns[2]]
                heapq.heappush(heap, [sum, lastAns[1] + 1, lastAns[2]])
            if (lastAns[2] + 1 < len(nums2)):
                sum = nums1[lastAns[1]] + nums2[lastAns[2] + 1]
                heapq.heappush(heap, [sum, lastAns[1], lastAns[2] + 1])

        return ans
