        int n = nums1.size(); 
        int m = nums2.size();

        priority_queue<ppi, vector<ppi>, greater<ppi>> q;
        vector<vector<int>> res;

        for(int i = 0; i < k && i < n; i++)
            q.push({nums1[i]+nums2[0], {i, 0}});

        while(!q.empty() && k--){
            auto [i, j] = q.top().second;
            q.pop();
            res.push_back({nums1[i], nums2[j]});

            if(j < m-1)
                q.push({nums1[i]+nums2[j+1], {i, j+1}});
        }
        return res;
    }
};

