class Solution {
public:
    vector<vector<int>> kSmallestPairs(vector<int>& nums1, vector<int>& nums2, int k) {
        if(nums1.size()==0 || nums2.size()==0){
            return {};
        }
        if(k>nums1.size()*nums2.size()){
            k = nums1.size()*nums2.size();
        }
        priority_queue<vector<int>> q;
        for(int i=0; i<nums1.size(); i++){
            for(int j=0; j<nums2.size(); j++){
                vector<int> sum;
                sum.push_back(nums1[i]+nums2[j]);
                sum.push_back(nums1[i]);
                sum.push_back(nums2[j]);
                if(q.size()<k){
                    q.push(sum);
                }
                else if(q.size()==k && q.top()[0]>nums1[i]+nums2[j]){
                    q.pop();
                    q.push(sum);
                }
            }
        }

        vector<vector<int>> res(k);
        for(int i=k-1; i>=0; i--){
            res[i].push_back(q.top()[1]);
            res[i].push_back(q.top()[2]);
            q.pop();
        }
        return res;
    }
};
